import Button from "../UI/Button";
import classes from "./Modal.css";

const Modal = (props) => {
  const removeModal = () => {};
  return (
    <Card className={classes.modal}>
      <header className={classes.header}>
        <h2>{props.title}</h2>
      </header>
      <div className={classes.content}>
        <p>{props.message}</p>
      </div>
      <footer className={classes.actions}>
        <Button>Close</Button>
      </footer>
    </Card>
  );
};

export default Modal;
